import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './sports.css';
  
  const SportsEvents = () => {
    const [sportsEvents, setSportsEvents] = useState([]);
  
    // Manually added events
    const manualEvents = [
      { id: 1, imageUrl: 'https://assets-in.bmscdn.com/discovery-catalog/events/tr:w-400,h-600,bg-CCCCCC:w-400.0,h-660.0,cm-pad_resize,bg-000000,fo-top:l-text,ie-U3VuLCAxNSBTZXA%3D,fs-29,co-FFFFFF,ly-612,lx-24,pa-8_0_0_0,l-end/et00358311-xtmewlcxzt-portrait.jpg', name: 'Chennai Chess Club - Blitz Event' },
      { id: 2, imageUrl: 'https://assets-in.bmscdn.com/discovery-catalog/events/tr:w-400,h-600,bg-CCCCCC:w-400.0,h-660.0,cm-pad_resize,bg-000000,fo-top:l-text,ie-TW9uLCAxNiBTZXA%3D,fs-29,co-FFFFFF,ly-612,lx-24,pa-8_0_0_0,l-end/et00408550-ytjusmstqc-portrait.jpg', name: 'Chess Club - Connect Online Chess Classes' },
      { id: 3, imageUrl: 'https://assets-in.bmscdn.com/discovery-catalog/events/tr:w-400,h-600,bg-CCCCCC:w-400.0,h-660.0,cm-pad_resize,bg-000000,fo-top:l-text,ie-U3VuLCAxNSBTZXAgb253YXJkcw%3D%3D,fs-29,co-FFFFFF,ly-612,lx-24,pa-8_0_0_0,l-end/et00409557-rcavcmcver-portrait.jpg', name: 'Rapid Chess Tournament Championship' },
      { id: 4, imageUrl: 'https://assets-in.bmscdn.com/discovery-catalog/events/tr:w-400,h-600,bg-CCCCCC:w-400.0,h-660.0,cm-pad_resize,bg-000000,fo-top:l-text,ie-V2VkLCAyIE9jdA%3D%3D,fs-29,co-FFFFFF,ly-612,lx-24,pa-8_0_0_0,l-end/et00409087-dveqzwcyhn-portrait.jpg', name: 'Chess Championship 2024' },
    ];
  
    // Fetch events from the backend
    const fetchSportsEvents = async () => {
      try {
        const response = await fetch('http://localhost:5000/products');
        const events = await response.json();
  
        // Combine manually added events with API fetched events
        setSportsEvents([...manualEvents, ...events]);
      } catch (error) {
        console.error('Error fetching events:', error);
      }
    };
  
    useEffect(() => {
      fetchSportsEvents(); // Fetch events when component loads
    }, []);
  
    return (
      <div className="sports-events-container">
        <h2>Sports Events</h2>
        <div className="grid-container">
          {sportsEvents.map(event => (
            <Link
              key={event.id}
              to={`/sports/${event.id}`}
              className="event-card"
            >
              <img src={event.imageUrl} alt={event.name} className="event-image" />
              <h3>{event.name}</h3>
            </Link>
          ))}
        </div>
      </div>
    );
  };
  
  export default SportsEvents;
