import React, { useState } from "react";
import axios from 'axios';
import './AdminDashboard.css';

const AdminAddProduct = () => {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [description, setDescription] = useState('');
  const [imageUrl, setImageUrl] = useState('');
  const [error, setError] = useState(''); // Added state to handle error messages

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/admin/add-product', {
        name,
        price,
        description,
        imageUrl,
      }, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('adminToken')}`, // Ensure to set your JWT token here
        },
      });

      if (response.status === 201) {
        alert('Product added successfully!');
        setName('');
        setPrice('');
        setDescription('');
        setImageUrl('');
      }
    } catch (error) {
      console.error('There was an error adding the product!', error);
      setError('Failed to add product. Please try again.'); // Set error message for user feedback
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Add New Product</h2>
      {error && <p className="error">{error}</p>} {/* Display error message if exists */}
      <div>
        <label>Name:</label>
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
      </div>
      <div>
        <label>Price:</label>
        <input type="number" value={price} onChange={(e) => setPrice(e.target.value)} required />
      </div>
      <div>
        <label>Description:</label>
        <textarea value={description} onChange={(e) => setDescription(e.target.value)} required></textarea>
      </div>
      <div>
        <label>Image Url:</label>
        <input type="text" value={imageUrl} onChange={(e) => setImageUrl(e.target.value)} required />
      </div>
      <button type="submit">Add Product</button>
    </form>
  );
};

export default AdminAddProduct;
