// pages/AdminLogin.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // For navigation after login
import './AdminLogin.css';
import axios from 'axios'; // Ensure axios is installed for API calls

const AdminLogin = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate(); // To navigate to the admin page after login

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const response = await axios.post('http://localhost:5000/admin/login', { // Adjust URL as needed
        email,
        password,
      });
      
      const token = response.data.token; // Get token from response
      localStorage.setItem('adminToken', token); // Save token in local storage
      alert('Admin Logged In Successfully!');
      navigate('/admin/dashboard'); // Redirect to Admin Dashboard
    } catch (err) {
      setError('Login failed. Please check your credentials.');
    }
  };

  return (
    <div className="admin-login-container">
      <h2>Admin Login</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Login</button>
        {error && <p className="error-message">{error}</p>}
      </form>
    </div>
  );
};

export default AdminLogin;
