import React, { useState } from 'react';
import './BookingForm.css';  // Ensure you have this CSS file for styling

const BookingForm = ({ onClose }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    tickets: '',
    chairs: []
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert('Tickets booked successfully!');
    console.log(formData);
    onClose();
  };

  const generateChairs = () => {
    let chairs = [];
    for (let i = 1; i <= 100; i++) {
      chairs.push(i);
    }
    return chairs;
  };

  return (
    <div className="booking-form">
      <div className="overlay" onClick={onClose}></div>
      <div className="form-container">
        <h2>Book Tickets</h2>
        <form onSubmit={handleSubmit}>
          <label>
            Name:
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Email:
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Number of Tickets:
            <input
              type="number"
              name="tickets"
              value={formData.tickets}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Select Chairs:
            <div className="chair-selection">
              {generateChairs().map((chair) => (
                <div key={chair} className="chair">
                  <input
                    type="checkbox"
                    name="chairs"
                    value={chair}
                    checked={formData.chairs.includes(chair)}
                    onChange={(e) => {
                      const newChairs = e.target.checked
                        ? [...formData.chairs, e.target.value]
                        : formData.chairs.filter(c => c !== e.target.value);
                      setFormData(prev => ({ ...prev, chairs: newChairs }));
                    }}
                  />
                  <label>{chair}</label>
                </div>
              ))}
            </div>
          </label>
          <button type="submit">Submit</button>
        </form>
      </div>
    </div>
  );
};

export default BookingForm;
