// pages/AdminDashboard.js
import React, { useEffect, useState } from 'react';
import axios from 'axios'; // Ensure axios is installed for API calls
import { Link } from 'react-router-dom';

const AdminDashboard = () => {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const token = localStorage.getItem('adminToken'); // Get token from local storage
        const response = await axios.get('http://localhost:5000/products', { // Adjust URL as needed
          headers: { Authorization: `Bearer ${token}` }, // Include token in headers
        });
        setProducts(response.data);
      } catch (err) {
        setError('Failed to fetch products. Please try again.');
      }
    };

    fetchProducts();
  }, []);

  return (
    <div>
      <h2>Admin Dashboard</h2>
      <nav>
        <Link to="/admin/add-product">Add Product</Link>
        <Link to="/admin/view-bookings">View Bookings</Link> {/* Placeholder for future functionality */}
        <Link to="/admin/contact-requests">View Contact Requests</Link> {/* Placeholder for future functionality */}
      </nav>

      {error && <p className="error-message">{error}</p>}

      <h3>Products List</h3>
      {products.length === 0 ? (
        <p>No products available.</p>
      ) : (
        <ul>
          {products.map((product) => (
            <li key={product._id}>
              <strong>{product.name}</strong> - ${product.price}
              <p>{product.description}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default AdminDashboard;
