// Tickets.js
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './sports.css'; // Import your CSS

const Tickets = () => {
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchTickets = async () => {
      try {
        const response = await fetch('http://localhost:5000/tickets'); // Fetch tickets from the backend
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        setTickets(data.tickets);
      } catch (error) {
        console.error("Fetch error: ", error); // Log the error for debugging
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchTickets();
  }, []);

  if (loading) {
    return <p>Loading...</p>; // Loading state
  }

  if (error) {
    return <p>{error}</p>; // Error state
  }

  return (
    <div className="tickets-container">
      <h2>Available Tickets</h2>
      <div className="grid-container">
        {tickets.map((ticket, index) => (
          <Link
            key={index}
            to={`/tickets/${index + 1}`} // Example of routing; adjust as needed
            className="ticket-card"
          >
            <img src={ticket.imageUrl} alt={ticket.name} className="ticket-image" />
            <h3>{ticket.name}</h3>
            <p>Price: ${ticket.price.toFixed(2)}</p> {/* Display ticket price */}
          </Link>
        ))}
      </div>
    </div>
  );
};

export default Tickets;
