// src/pages/TicketPage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const movies = [
  { id: 1, name: 'Movie 1', description: 'Description 1', rating: 4.5, amount: 200, imageUrl: 'https://via.placeholder.com/150' },
  { id: 2, name: 'Movie 2', description: 'Description 2', rating: 3.5, amount: 150, imageUrl: 'https://via.placeholder.com/150' },
  { id: 3, name: 'Movie 3', description: 'Description 3', rating: 5.0, amount: 250, imageUrl: 'https://via.placeholder.com/150' },
  { id: 4, name: 'Movie 4', description: 'Description 4', rating: 4.0, amount: 180, imageUrl: 'https://via.placeholder.com/150' },
  { id: 5, name: 'Movie 5', description: 'Description 5', rating: 3.8, amount: 160, imageUrl: 'https://via.placeholder.com/150' },
  { id: 6, name: 'Movie 6', description: 'Description 6', rating: 4.2, amount: 190, imageUrl: 'https://via.placeholder.com/150' },
  { id: 7, name: 'Movie 7', description: 'Description 7', rating: 3.9, amount: 170, imageUrl: 'https://via.placeholder.com/150' },
  { id: 8, name: 'Movie 8', description: 'Description 8', rating: 4.7, amount: 220, imageUrl: 'https://via.placeholder.com/150' },
  { id: 9, name: 'Movie 9', description: 'Description 9', rating: 4.1, amount: 210, imageUrl: 'https://via.placeholder.com/150' },
  { id: 10, name: 'Movie 10', description: 'Description 10', rating: 3.6, amount: 140, imageUrl: 'https://via.placeholder.com/150' },
];

const TicketPage = () => {
  const [cart, setCart] = useState([]);
  const navigate = useNavigate();

  const addToCart = (movie) => {
    setCart([...cart, movie]);
    alert(`${movie.name} added to cart!`);
    navigate('/cart', { state: { cart: [...cart, movie] } });
  };

  return (
    <div>
      <h1>Ticket Page</h1>
      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
        {movies.map((movie) => (
          <div key={movie.id} style={{ margin: '10px', border: '1px solid #ddd', padding: '10px', width: '200px', textAlign: 'center' }}>
            <img src={movie.imageUrl} alt={movie.name} style={{ width: '100%', height: 'auto' }} />
            <h2>{movie.name}</h2>
            <p>{movie.description}</p>
            <p>Rating: {movie.rating}</p>
            <p>Amount: â‚¹{movie.amount}</p>
            <button onClick={() => addToCart(movie)}>Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TicketPage;
