// Cart.js
import React from 'react';
import { useLocation, Link } from 'react-router-dom';

function Cart() {
  const location = useLocation();
  const cart = location.state?.cart || []; // Default to an empty array if cart is not provided

  return (
    <div className="cart-page">
      <h1>Cart</h1>
      {cart.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <ul>
          {cart.map((item, index) => (
            <li key={index}>
              <h3>{item.title}</h3>
              <p>Price: â‚¹{item.price}</p>
            </li>
          ))}
        </ul>
      )}
      <Link to="/">Back to Tickets</Link>
    </div>
  );
}

export default Cart;
