// AdminLogin.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // For navigation after login
import './AdminLogin.css';

const AdminLogin = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate(); // To navigate to the admin page after login

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');

    // Hardcoded admin credentials
    const adminCredentials = {
      email: 'admin#@gmail.com', // Change to your admin email
      password: '123qwe123' // Change to your admin password
    };

    if (email === adminCredentials.email && password === adminCredentials.password) {
      localStorage.setItem('adminToken', 'your-token'); // Save token or flag for admin access
      alert('Admin Logged In Successfully!');
      navigate('/admin/add-product'); // Redirect to product addition page
    } else {
      setError('Login failed. Please check your credentials.');
    }
  };

  return (
    <div className="admin-login-container">
      <h2>Admin Login</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Login</button>
        {error && <p className="error-message">{error}</p>}
      </form>
    </div>
  );
};

export default AdminLogin;
