// src/CartPage.js
import React from 'react';
import { useLocation, useHistory } from 'react-router-dom';

const CartPage = () => {
  const location = useLocation();
  const history = useHistory();
  const cart = location.state ? location.state.cart : [];

  const handleBack = () => {
    history.push('/');
  };

  return (
    <div>
      <h1>Cart Page</h1>
      <button onClick={handleBack}>Back to Ticket Page</button>
      <div>
        {cart.length === 0 ? (
          <p>No movies in the cart.</p>
        ) : (
          cart.map((movie, index) => (
            <div key={index} style={{ margin: '10px', border: '1px solid #ddd', padding: '10px' }}>
              <h2>{movie.name}</h2>
              <p>{movie.description}</p>
              <p>Rating: {movie.rating}</p>
              <p>Amount: â‚¹{movie.amount}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default CartPage;
