// pages/AdminDashboard.js
import React, { useState } from 'react';
import './AdminDashboard.css'; // Import the CSS file

const AdminDashboard = () => {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [description, setDescription] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');

    const productData = {
      name,
      price: parseFloat(price),
      description,
    };

    try {
      const response = await fetch('http://localhost:5000/admin/add-product', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('adminToken')}` // Include the token
        },
        body: JSON.stringify(productData),
      });

      if (response.ok) {
        const data = await response.json();
        setMessage(data.message);
        // Clear form
        setName('');
        setPrice('');
        setDescription('');
      } else {
        const errorData = await response.json();
        setMessage(errorData.message || 'Failed to add product');
      }
    } catch (error) {
      setMessage('Server error, please try again later.');
    }
  };

  return (
    <div className="admin-dashboard">
      <h2>Admin Dashboard</h2>
      <p>Welcome to the admin dashboard! You can manage your products here.</p>

      <h3>Add a New Product</h3>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Product Name:</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Price:</label>
          <input
            type="number"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Description:</label>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          ></textarea>
        </div>
        <button type="submit">Add Product</button>
      </form>
      {message && <p className={message.includes('successfully') ? 'success-message' : 'error-message'}>{message}</p>} {/* Display messages */}
    </div>
  );
};

export default AdminDashboard;
