import React, { useState } from 'react';
import ReactDOM from 'react-dom';

// Sample movie data
const movieData = [
  {
    id: 1,
    name: "The Avengers",
    description: "A group of superheroes band together to save the world.",
    rating: 8.0,
    rate: 500,
  },
  {
    id: 2,
    name: "Inception",
    description: "A skilled thief is offered a chance to have his past crimes forgiven.",
    rating: 8.8,
    rate: 600,
  },
  // Add more movies as needed
];

// Ticket Page Component
const TicketPage = ({ addToCart }) => {
  return (
    <div>
      <h1>Ticket Booking Platform</h1>
      <div className="movies">
        {movieData.map((movie) => (
          <div key={movie.id} className="movie">
            <h2>{movie.name}</h2>
            <p>{movie.description}</p>
            <p>Rating: {movie.rating}</p>
            <p>Rate: ₹{movie.rate}</p>
            <button onClick={() => addToCart(movie)}>Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
};

// Cart Page Component
const CartPage = ({ cartItems }) => {
  return (
    <div>
      <h1>Cart</h1>
      {cartItems.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <div className="cart">
          {cartItems.map((movie) => (
            <div key={movie.id} className="cart-item">
              <h2>{movie.name}</h2>
              <p>Rate: ₹{movie.rate}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

// Main App Component
const Tickets= () => {
  const [cart, setCart] = useState([]);
  const [showCart, setShowCart] = useState(false);

  const addToCart = (movie) => {
    setCart([...cart, movie]);
  };

  return (
    <div>
      {showCart ? (
        <CartPage cartItems={cart} />
      ) : (
        <TicketPage addToCart={addToCart} />
      )}
      <button onClick={() => setShowCart(!showCart)}>
        {showCart ? 'Back to Movies' : 'View Cart'}
      </button>
    </div>
  );
};
export default 

// Render the App component
ReactDOM.render(<App />, document.getElementById('root'));
