import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';

const BookedTickets = () => {
  const location = useLocation();
  const { formData, movie } = location.state || {}; // Retrieve passed state
  const [isCanceled, setIsCanceled] = useState(false); // Track if the ticket is canceled

  const handleCancel = () => {
    alert('Ticket canceled successfully!');
    setIsCanceled(true); // Mark the ticket as canceled
  };

  if (!formData || !movie) {
    return <p>No booking information available.</p>;
  }

  if (isCanceled) {
    return <p>Your ticket has been canceled.</p>; // Display message after cancellation
  }

  return (
    <div className="booked-tickets">
      <h1>Booking Confirmation</h1>
      <div className="booking-details">
        <p><strong>Movie:</strong> {movie.title}</p>
        <p><strong>Name:</strong> {formData.name}</p>
        <p><strong>Email:</strong> {formData.email}</p>
        <p><strong>Number of Tickets:</strong> {formData.tickets}</p>
        <p><strong>Chairs:</strong> {formData.chairs.join(', ')}</p>
        <p><strong>Payment Method:</strong> {formData.paymentMethod}</p>
      </div>
      <button onClick={handleCancel} className="cancel-button">
        Cancel Ticket
      </button>
    </div>
  );
};

export default BookedTickets;
