// AdminDashboard.js
const addTicket = async (ticketData) => {
  try {
    const response = await fetch('http://localhost:5000/admin/add-ticket', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${localStorage.getItem('adminToken')}`, // Include the token
      },
      body: JSON.stringify(ticketData),
    });

    if (response.ok) {
      const data = await response.json();
      return { success: true, message: data.message };
    } else {
      const errorData = await response.json();
      return { success: false, message: errorData.message || 'Failed to add ticket' };
    }
  } catch (error) {
    return { success: false, message: 'Server error, please try again later.' };
  }
};
