import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './sports.css';

const SportsEvents = () => {
  const [sportsEvents, setSportsEvents] = useState([]);
  const [loading, setLoading] = useState(true); // Track loading state
  const [error, setError] = useState(null); // Track any errors

  // Fetch sports events from the backend
  useEffect(() => {
    const fetchSportsEvents = async () => {
      try {
        const response = await fetch('http://localhost:5000/sports-events'); // Change this to your actual API endpoint
        if (!response.ok) {
          throw new Error('Failed to fetch sports events');
        }
        const data = await response.json();
        setSportsEvents(data); // Assuming the data is an array of events
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false); // Loading is complete
      }
    };

    fetchSportsEvents();
  }, []); // Empty dependency array to run only once on component mount

  if (loading) {
    return <p>Loading events...</p>; // Show a loading message while data is being fetched
  }

  if (error) {
    return <p>Error: {error}</p>; // Show an error message if something went wrong
  }

  return (
    <div className="sports-events-container">
      <h2>Sports Events</h2>
      <div className="grid-container">
        {sportsEvents.length === 0 ? (
          <p>No events available.</p>
        ) : (
          sportsEvents.map(event => (
            <Link
              key={event.id}
              to={`/sports/${event.id}`} // Navigate to the detail page with ID
              className="event-card"
            >
              <img src={event.imageUrl} alt={event.title} className="event-image" />
              <h3>{event.title}</h3>
            </Link>
          ))
        )}
      </div>
    </div>
  );
};

export default SportsEvents;
