import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

// Sample movie data
const movies = [
  // Include your movie data here
  // Make sure to have movie details (id, title, image) similar to previous data
  {
    id: 1,
    title: 'The Greatest of All Time',
    image: 'https://assets-in.bmscdn.com/discovery-catalog/events/tr:w-400,h-600,bg-CCCCCC:w-400.0,h-660.0,cm-pad_resize,bg-000000,fo-top:l-image,i-discovery-catalog@@icons@@star-icon-202203010609.png,lx-24,ly-615,w-29,l-end:l-text,ie-OC8xMCAgMTE1LjZLIFZvdGVz,fs-29,co-FFFFFF,ly-612,lx-70,pa-8_0_0_0,l-end/et00401439-xdtxcmtyux-portrait.jpg'
  },
  // Add other movie data here
];

const BookedTickets = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { formData, movieId } = location.state || {};

  // Find the movie details based on movieId
  const movie = movies.find((m) => m.id === movieId);

  const handleWithdraw = () => {
    // Implement logic to withdraw the ticket here
    alert('Ticket withdrawn successfully!');
    // Redirect or update state as needed
    navigate('/');
  };

  return (
    <div className="booked-tickets">
      <h1>Booking Confirmation</h1>
      <p>Thank you for booking!</p>
      {movie && (
        <div className="movie-details">
          <h2>Movie Details</h2>
          <img src={movie.image} alt={movie.title} className="movie-image" />
          <p>{movie.title}</p>
        </div>
      )}
      <h2>Details:</h2>
      <p>Name: {formData?.name}</p>
      <p>Email: {formData?.email}</p>
      <p>Number of Tickets: {formData?.tickets}</p>
      <p>Payment Method: {formData?.paymentMethod}</p>
      <p>Selected Chairs: {formData?.chairs.join(', ')}</p>
      <button className="withdraw-btn" onClick={handleWithdraw}>
        Withdraw Ticket
      </button>
    </div>
  );
};

export default BookedTickets;
