import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './sports.css';

const SportsEvents = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch('http://localhost:5000/products'); // Fetch products from the backend
        if (!response.ok) {
          throw new Error('Failed to fetch products');
        }
        const data = await response.json();
        setProducts(data.products);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  if (loading) {
    return <p>Loading...</p>; // Loading state
  }

  if (error) {
    return <p>{error}</p>; // Error state
  }

  return (
    <div className="sports-events-container">
      <h2>Sports Events</h2>
      <div className="grid-container">
        {products.map((product, index) => (
          <Link
            key={index}
            to={`/sports/${index + 1}`} // Example of routing; adjust as needed
            className="event-card"
          >
            <img src={product.imageUrl} alt={product.name} className="event-image" />
            <h3>{product.name}</h3>
            <p>Price: ${product.price.toFixed(2)}</p> {/* Display product price */}
          </Link>
        ))}
      </div>
    </div>
  );
};

export default SportsEvents;
