// pages/ProductAdding.js
import React, { useState } from 'react';
import axios from 'axios'; // Make sure to install axios if not already done

const ProductAdding = () => {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [description, setDescription] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      const token = localStorage.getItem('adminToken'); // Get token from local storage
      const response = await axios.post(
        'http://localhost:5000/admin/add-product', // Adjust the URL as needed
        { name, price, description },
        { headers: { Authorization: `Bearer ${token}` } } // Include token in headers
      );

      setSuccess(response.data.message);
    } catch (err) {
      setError('Failed to add product. Please try again.');
    }
  };

  return (
    <div>
      <h2>Add Product</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
        </div>
        <div>
          <label>Price:</label>
          <input type="number" value={price} onChange={(e) => setPrice(e.target.value)} required />
        </div>
        <div>
          <label>Description:</label>
          <textarea value={description} onChange={(e) => setDescription(e.target.value)} required />
        </div>
        <button type="submit">Add Product</button>
      </form>
      {error && <p className="error-message">{error}</p>}
      {success && <p className="success-message">{success}</p>}
    </div>
  );
};

export default ProductAdding;
