import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './sports.css';

const SportsEvents = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch products from the backend when the component mounts
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch('http://localhost:5000/products', { // Adjust the URL as per your backend
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });

        if (response.ok) {
          const data = await response.json();
          setProducts(data.products); // Assuming the API returns an array of products
        } else {
          console.error('Failed to fetch products');
        }
      } catch (error) {
        console.error('Error fetching products:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  if (loading) {
    return <p>Loading products...</p>;
  }

  return (
    <div className="sports-events-container">
      <h2>Sports Events & Tickets</h2>
      <div className="grid-container">
        {products.map(product => (
          <Link
            key={product.id}
            to={`/sports/${product.id}`} // Navigate to the detail/booking page with product ID
            className="event-card"
          >
            <img src={product.imageUrl} alt={product.name} className="event-image" />
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p>Price: ${product.price}</p>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default SportsEvents;
