// src/pages/CartPage.js
import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const CartPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [cart, setCart] = useState([]);

  useEffect(() => {
    if (location.state && location.state.cart) {
      setCart(location.state.cart);
    } else {
      // Redirect or handle case when there's no cart state
      navigate('/');
    }
  }, [location.state, navigate]);

  const removeFromCart = (id) => {
    setCart(cart.filter(movie => movie.id !== id));
  };

  const calculateTotal = () => {
    return cart.reduce((total, movie) => total + movie.amount, 0);
  };

  return (
    <div>
      <h1>Cart Page</h1>
      {cart.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <div>
          {cart.map((movie) => (
            <div key={movie.id} style={{ margin: '10px', border: '1px solid #ddd', padding: '10px', width: '200px', textAlign: 'center' }}>
              <img src={movie.imageUrl} alt={movie.name} style={{ width: '100%', height: 'auto' }} />
              <h2>{movie.name}</h2>
              <p>{movie.description}</p>
              <p>Rating: {movie.rating}</p>
              <p>Amount: ₹{movie.amount}</p>
              <button onClick={() => removeFromCart(movie.id)}>Remove from Cart</button>
            </div>
          ))}
          <h2>Total Amount: ₹{calculateTotal()}</h2>
        </div>
      )}
    </div>
  );
};

export default CartPage;
